package week1.day2;

public class Learnbreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for (int i = 0; i < 100; i++) {
		if(i==25) { //comes/or jumps of the current loop
		break;		
	}
	System.out.println(i);
	}
	System.out.println("Step1");
	
	
	
	}
}